package logica;

public class Operador {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int nota1 = 30;
int nota2 = 30;
//igual
if(nota1 == nota2) {
	System.out.println("notas iguais");
}else {
	System.out.println("notas diferentes");
}
//diferentes
	if(nota1 != nota2) {
		System.out.println("notas diferentes");
	}else {
		System.out.println("notas iguais");
	}
	}
	

}
